import logo from './logo.svg';
import './App.css';
import React,{useState,useEffect} from "react";
import axios from "axios"
import {format} from "date-fns"

function App() {
  const [seriesList, setSeriesList] = useState([]);   


  const fetchbyStatus = async (e)=>{
       e.preventDefault();
       var status = e.target.value;
       console.log(status)
       const data = await axios.get('/Series/find=name=&app=&status='+status)
       const {series} = data.data
       setSeriesList(series);
       console.log(data)

  }

  /*const fetchNames = async () => {
    const data = await axios.get('/names')
    const {names} = data.data
    setNameList(names);
    console.log(data)
  }*/

  /*useEffect(() => {
    fetchNames();     
  }, []);
*/
  return (
    <div className="App">
        <table>     
            <tr>
               <td>
                 <input type="text" name="BuscaNome" id="BuscaNome">
                       </input>
               </td>
               <td>
                <input type="text" name="BuscaApp" id="BuscaApp"></input>
              </td>
               <td>
                  <select id='BuscaStatus' name='BuscaStatus' onChange={fetchbyStatus}>
                              <option value=''></option>
                                  <option value='Watched'>Watched</option>
                                  <option value='Watching'>Watching</option>
                                  <option value='Waiting'>Waiting</option>
                  </select>              
               </td>
            </tr>
        </table> 
        <hr></hr>
        <table>
        <tr>
            <td>Ano</td>
            <td>Serie</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>Temporada</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>App</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>Status</td>
        </tr>
          {seriesList.map(serie => {
              return (
                <tr>
                  <td>{serie.Year}</td>
                  <td>{serie.SeriesName}</td>
                  <td>{serie.Season}</td>
                  <td>{serie.App}</td>
                  <td>{serie.Status} 
                 <input type="hidden" id="idStatus" value="{serie.id}"></input></td>
                </tr>
              )
          })}
        </table>    

    </div>
  );
}

export default App;
