import logo from './logo.svg';
import './App.css';
import React,{useState,useEffect} from "react";
import axios from "axios"
import {format} from "date-fns"


function App() {
  const [seriesList, setSeriesList] = useState([]);   
  const [cad,setCad] = useState("");  
  const [search,setSearch] = useState("");  

  const clickcad = async (e)=>{
      e.preventDefault();
      setCad(true)
      setSearch(false)
}

  const saveSerie = async (e)=>{
     e.preventDefault();
  }

  const fetchbyName = async (e)=>{
    if (e.which == 13) {
      e.preventDefault();
      setCad(false)
      setSearch(true)
      var name = e.target.value;
      console.log(name)
      const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name='+name+'&app=&status=')
      const {series} = data.data
      setSeriesList(series);
      console.log(data)
    }
}

const fetchbyApp = async (e)=>{
  if (e.which == 13) {
    e.preventDefault();
    setCad(false)
    setSearch(true)
    var app = e.target.value;
    console.log(app)
    const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name=&app='+app+'&status=')
    const {series} = data.data
    setSeriesList(series);
    console.log(data)
  }
}

  const fetchbyStatus = async (e)=>{
       e.preventDefault();
       setCad(false)
       setSearch(true)
       var status = e.target.value;
       console.log(status)
       const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name=&app=&status='+status)
       const {series} = data.data
       setSeriesList(series);
       console.log(data)

  }

  const renderCad = () => {
    if (cad) {
      return <table align='center'>
         <tr>
          <td>Year</td>
          <td><input type="text" name="Year"></input></td>
        </tr> 
        <tr>
          <td>Series Name</td>
          <td><input type="text" name="seriesName"></input></td>
        </tr>
        <tr>
          <td>Season</td>
          <td><input type="text" name="Season"></input></td>
        </tr>
        <tr>
          <td>Provider</td>
          <td><input type="text" name="Provider"></input></td>
        </tr>
        <tr>
          <td>App</td>
          <td><input type="text" name="App"></input></td>
        </tr>
        <tr>
          <td>Status</td>
          <td><select id='cboMed' name='Status' >
            <option value='Watched'>Watched</option>
            <option value='Watching'>Watching</option>
            <option value='Waiting'>Waiting</option>
        </select></td>
        </tr>
        <tr>
          <td colSpan={2}>
          <button name='salvar' width='100' onClick={saveSerie} >Salvar</button>
          </td>
        </tr>
      </table>;  
    }
  }

  const renderSeries =() => {
    if( search ){
     return <table>
        <tr>
            <td>Ano</td>
            <td>Serie</td>
            <td>Temporada</td>
            <td>App</td>
            <td>Status</td>
        </tr>
          {seriesList.map(serie => {
              return (
                <tr>
                  <td>{serie.Year}</td>
                  <td>{serie.SeriesName}</td>
                  <td>{serie.Season}</td>
                  <td>{serie.App}</td>
                  <td>{serie.Status} 
                 <input type="hidden" id="idStatus" value="{serie.id}"></input></td>
                </tr>
              )
          })}
        </table>;    
    }
  } 

  const renderSaved =() => {
    if( saved ){
     return <table>
        <tr>
            <td>Ano</td>
        </tr>
          {seriesList.map(serie => {
              return (
                <tr>
                  <td>{serie.Year}</td>
                  <td>{serie.SeriesName}</td>
                  <td>{serie.Season}</td>
                  <td>{serie.App}</td>
                  <td>{serie.Status} 
                 <input type="hidden" id="idStatus" value="{serie.id}"></input></td>
                </tr>
              )
          })}
        </table>;    
    }
  } 
  /*const fetchNames = async () => {
    const data = await axios.get('/names')
    const {names} = data.data
    setNameList(names);
    console.log(data)
  }*/

  /*useEffect(() => {
    fetchNames();     
  }, []);
*/
  return (
    <div className="App-header">
        <h3>Dados de SÃ©ries</h3><img src={logo} className="App-logo" alt="logo" />
        <table width={500}>     
            <tr>
               <td>
                 <input type="text" name="BuscaNome" id="BuscaNome" onKeyPress={fetchbyName} placeholder="Busca por Nome">
                       </input>
               </td>
               <td>
                <input type="text" name="BuscaApp" id="BuscaApp" onKeyPress={fetchbyApp} placeholder="Busca por App"></input>
              </td>
               <td width={120}>Busca por Status</td>
               <td>  <select id='BuscaStatus' name='BuscaStatus' onChange={fetchbyStatus}>
                              <option value=''></option>
                                  <option value='Watched'>Watched</option>
                                  <option value='Watching'>Watching</option>
                                  <option value='Waiting'>Waiting</option>
                  </select>              
               </td>
               <td>
               <button name='Cadastro' onClick={clickcad} width='20'>Inserir </button>
               </td>
            </tr>
        </table> 
        <hr></hr>
        {renderCad()}         
        {renderSeries()}  
        {renderSaved()}    
    </div>
  );
}

export default App;
