       e.preventDefault();
       var status = e.target.value;
       console.log(stockname)
       const data = await axios.get('/Series/find=name=&app=&status='+status)
       const {series} = data.data
       setSeriesList(series);
       console.log(data)

  }

  const fetchNames = async () => {
    const data = await axios.get('/names')
    const {names} = data.data
    setNameList(names);
    console.log(data)
  }

  useEffect(() => {
    fetchNames();     
  }, []);

  return (
    <div className="App">
        <table>     
            <tr>
               <td>
                 <input type="text" name="BuscaNome" id="BuscaNome"
                       placeholder="Busca por nome" class="form-control"
                       value="{{ request.form['BuscaNome'] }}"></input>
               </td>
               <td>
                <input type="text" name="BuscaApp" id="BuscaApp"
                      placeholder="Busca por Streaming" class="form-control"
                      value="{{ request.form['BuscaApp'] }}"></input>
              </td>
               <td>
                  <select id='BuscaStatus' name='BuscaStatus' placeholder="Busca por status" class="form-control"
                              value="{{ request.form['BuscaStatus'] }}">
                              <option value=''></option>
                                  <option value='Watched'>Watched</option>
                                  <option value='Watching'>Watching</option>
                                  <option value='Waiting'>Waiting</option>
                  </select>              
               </td>
            </tr>
        </table> 
    <hr></hr>
        <table>
        <tr style='background-color:slategray; font-style: italic;font-size: 14;font-weight: bold;'>
            <td style='border: 0px solid black;border-collapse: collapse;'>Ano</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>Serie</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>Temporada</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>App</td>
            <td style='border: 0px solid black;border-collapse: collapse;'>Status</td>
        </tr>
          {seriesList.map(serie => {
              return (
                <tr>"": "Disney +",
                "Season": 1,
                "SeriesName": "She Hulk",
                "": "Watching",
                  <td style='border: 0px solid black;border-collapse: collapse;'>{serie.Year}</td>
                  <td style='border: 0px solid black;border-collapse: collapse;'>{serie.Year}</td>
                  <td style='border: 0px solid black;border-collapse: collapse;'>{serie.Year}</td>
                  <td style='border: 0px solid black;border-collapse: collapse;'>{serie.App}</td>
                 <td style='border: 0px solid black;border-collapse: collapse;'>{serie.Status} 
                 <input type="hidden" id="idStatus" value="{serie.id}"></input></td>
                  <td>{stock.data}</td>
                  <td>{stock.Name}</td>
                  <td>{stock.Close}</td>
                </tr>
              )
          })}
        </table>    

    </div>
  );
}

export default App;
