            <td>Provider</td>
            <td>Status</td>
            <td></td>
        </tr>
          {seriesList.map(serie => {
              return ( <tr>
                  <td><input type="text" name="Year" value={serie.Year} onChange={(event) => setYear(event.target.value)}></input></td>
