import logo from './logo.svg';
import './App.css';
import React,{useState,useEffect} from "react";
import axios from "axios"
import {format} from "date-fns"


function App() {
  const [seriesList, setSeriesList] = useState([]);   
  const [cad,setCad] = useState("");  
  const [search,setSearch] = useState("");  

  const clickcad = async (e)=>{
      e.preventDefault();
      setCad(true)
      setSearch(false)
}

  const fetchbyName = async (e)=>{
    if (e.which == 13) {
      e.preventDefault();
      setCad(false)
      setSearch(true)
      var name = e.target.value;
      console.log(name)
      const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name='+name+'&app=&status=')
      const {series} = data.data
      setSeriesList(series);
      console.log(data)
    }
}

const fetchbyApp = async (e)=>{
  if (e.which == 13) {
    e.preventDefault();
    setCad(false)
    setSearch(true)
    var app = e.target.value;
    console.log(app)
    const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name=&app='+app+'&status=')
    const {series} = data.data
    setSeriesList(series);
    console.log(data)
  }
}

  const fetchbyStatus = async (e)=>{
       e.preventDefault();
       setCad(false)
       setSearch(true)
       var status = e.target.value;
       console.log(status)
       const data = await axios.get('https://victor-mongodbapi.herokuapp.com/Series/find?name=&app=&status='+status)
       const {series} = data.data
       setSeriesList(series);
       console.log(data)

  }

  const renderCad = () => {
    if (cad) {
      return  <table>
          <form method="post">
    <div class="form-group">
        <label for="Year">Year</label>
        <input type="text" name="Year"
               placeholder="Post Year" class="form-control"
               value="{{ request.form['Year'] }}"></input>
    </div>
    <div class="form-group">
        <label for="SeriesName">SeriesName</label>
        <input type="text" name="SeriesName"
               placeholder="Post SeriesName" class="form-control"
               value="{{ request.form['SeriesName'] }}"></input>
    </div>
    <div class="form-group">
        <label for="Season">Season</label>
        <input type="text" name="Season"
               placeholder="Post Season" class="form-control"
               value="{{ request.form['Season'] }}"></input>
    </div>
    <div class="form-group">
        <label for="Provider">Provider</label>
        <input type="text" name="Provider"
               placeholder="Post Provider" class="form-control"
               value="{{ request.form['Provider'] }}"></input>
    </div>
    <div class="form-group">
        <label for="App">App</label>
        <input type="text" name="App"
               placeholder="Post App" class="form-control"
               value="{{ request.form['App'] }}"></input>
    </div>
    <div class="form-group">
        <label for="Status">Status</label>
        <select id='cboMed' name='Status' placeholder="Post Status" class="form-control"
        value="{{ request.form['Status'] }}">
            <option value='Watched'>Watched</option>
            <option value='Watching'>Watching</option>
            <option value='Waiting'>Waiting</option>
        </select>
    </div>
    <div class="form-group">
        <button type="submit" class="btn btn-primary">Submit</button>
    </div>
</form>
        </table>;  
    }
  }

  const renderSeries =() => {
    if( search ){
     return <table>
        <tr>
            <td>Ano</td>
            <td>Serie</td>
            <td>Temporada</td>
            <td>App</td>
            <td>Status</td>
        </tr>
          {seriesList.map(serie => {
              return (
                <tr>
                  <td>{serie.Year}</td>
                  <td>{serie.SeriesName}</td>
                  <td>{serie.Season}</td>
                  <td>{serie.App}</td>
                  <td>{serie.Status} 
                 <input type="hidden" id="idStatus" value="{serie.id}"></input></td>
                </tr>
              )
          })}
        </table>;    
    }
  } 
  /*const fetchNames = async () => {
    const data = await axios.get('/names')
    const {names} = data.data
    setNameList(names);
    console.log(data)
  }*/

  /*useEffect(() => {
    fetchNames();     
  }, []);
*/
  return (
    <div className="App-header">
        <h3>Dados de SÃ©ries</h3><img src={logo} className="App-logo" alt="logo" />
        <table width={500}>     
            <tr>
               <td>
                 <input type="text" name="BuscaNome" id="BuscaNome" onKeyPress={fetchbyName} placeholder="Busca por Nome">
                       </input>
               </td>
               <td>
                <input type="text" name="BuscaApp" id="BuscaApp" onKeyPress={fetchbyApp} placeholder="Busca por App"></input>
              </td>
               <td width={120}>Busca por Status</td>
               <td>  <select id='BuscaStatus' name='BuscaStatus' onChange={fetchbyStatus}>
                              <option value=''></option>
                                  <option value='Watched'>Watched</option>
                                  <option value='Watching'>Watching</option>
                                  <option value='Waiting'>Waiting</option>
                  </select>              
               </td>
               <td>
               <button name='Cadastro' onClick={clickcad} width='20'>Inserir </button>
               </td>
            </tr>
        </table> 
        <hr></hr>
        {renderCad()}         
        {renderSeries()}    
    </div>
  );
}

export default App;
